import { pickNonNull } from '../../pick-non-null.js';

/**
 * GET /iam/organisations/{ownerId}/api-keys
 * @param {Object} params
 * @param {String} params.undefined
 * @param {String} params.from
 * @param {String} params.size
 */
export function listOrganisationApiKeys(params) {
  // no multipath for /self or /organisations/{id}
  return Promise.resolve({
    method: 'get',
    url: `/v4/iam/organisations/${params.ownerId}/api-keys`,
    headers: { Accept: 'application/json' },
    queryParams: pickNonNull(params, ['from', 'size']),
    // no body
  });
}
